import sqlite from "sqlite";
import SQL from "sql-template-strings";

/**
 * returns a date formatted like `YYYY-MM-DD HH:mm:ss.sss`, suitable for sqlite
 **/
const nowForSQLite = () => new Date().toISOString().replace('T', ' ').replace('Z', '')
/**
 * Joins multiple statements. Useful for `WHERE x = 1 AND y = 2`, where number of arguments is variable.
 * Usage:
 * ```js
 * joinSQLStatementKeys( ["name", "age", "email"], { email:"x@y.c", name="Z"}, ", ")
 * ```
 * Will return an SQL statement corresponding to the string:
 * ```js
 * name="Z", email="x@y.c"
 * ```
 * @param {Array} keys an array of strings representing the properties you want to join 
 * @param {Object} values an object containing the values 
 * @param {string} delimiter a string to join the parts with
 * @param {string} keyValueSeparator a string to join the parts with
 * @returns {Statement} an SQL Statement object
 */
const joinSQLStatementKeys = (keys, values, delimiter, keyValueSeparator = '=') => {
  return keys
    .map(propName => {
      const value = values[propName];
      if (value !== null && typeof value !== "undefined") {
        return SQL``.append(propName).append(keyValueSeparator).append(SQL`${value}`);
      }
      return false;
    }).filter(Boolean)
    .reduce((prev, curr) => prev.append(delimiter).append(curr));
};




const initializeDatabase_categories = async () => {
  const db = await sqlite.open("./harakat.sqlite");

  //await db.migrate({ force: 'last' })
  /**
   * retrieves the contacts from the database
   * @function getContactsList
   * @param {string} orderBy an optional string that is either "name" or "email"
   * @returns {array} the list of contacts
   */

  const getCategories = async props => {


    try {
      let stmt = SQL`SELECT * FROM categories`;
      const rows = await db.all(stmt);
      return rows
    }
    catch (err) {
      console.log(err)
      throw new Error("could not retrieve list!")
    }
  };

  /**
   * retrieves the contacts from the database
   * @function getContact
   * @param {number} id id to search by
   * @returns {array} contact found
   */


  const createCategories = async props => {
    const text = props;
    console.log("createCategoriesWithName", props)
    if (!props || !text) {
      throw new Error("you must provide a text");
    }
    try {
      // const date = nowForSQLite();
      const result = await db.run(
        SQL`INSERT INTO categories (name) Values (${text})`
      );
      const id = result.stmt.lastID;
      return id;
    } catch (err) {
      console.log(err)
      throw new Error("cannot insert this teaxt");
    }
  };

  const deleteCategories = async (props) => {
    const id = props
    console.log('>>>>>>>>',id)
    try {
      const result = await db.run(
        SQL`Delete from categories where id = ${id} `
      );
      if (result.stmt.changes === 0) {
        throw new Error(`could not delete name with id = ${id}`);
      }
      return true;
    } catch (err) {
      throw new Error("could not delete name");
    }
  };

  const updateCategories = async (props) => {
    if (!props.id )  {

      throw new Error("you must provide an id");
    }
    try {

      const stmt = SQL`UPDATE categories SET name=${props.name} WHERE id=${props.id} `
      // .append( joinSQLStatementKeys( ["text"], props.text ))
      // .append(SQL` WHERE `)
      // .append(joinSQLStatementKeys(["id"],
      //    props.id)
      // );

      const result = await db.run(stmt);
      if (result.stmt.changes === 0) {
        console.log("hello===>")
        throw new Error(`could not update the categories with id = ${props.id}`);
      }
      return true;
    } catch (err) {
      throw new Error(`could not update the category with id = ${props.id}` + err.message);
    }
  };

  // const findUser = async props => {
  //   const { username, password } = props;
  //   try {
  //     const stmt = SQL`SELECT * FROM users WHERE name = ${username} AND password = ${password}`;
  //     const rows = await db.all(stmt);
  //     const user = rows[0]
  //     if (!user) {
  //       throw new Error('Incorrect username or password!')
  //     }
  //     else
  //       return user;

  //   }
  //   catch (err) {
  //     throw new Error('Could not perform operation!')
  //   }
  // }

  // const findUserByID = async id => {
  //   try {
  //     const stmt = SQL`SELECT * FROM users WHERE name = ${id}`;
  //     const rows = await db.all(stmt);
  //     const user = rows[0]
  //     if (!user) {
  //       throw new Error('Incorrect name!')
  //     }
  //     else
  //       return user;
  //   }
  //   catch (err) {
  //     throw new Error(err)
  //   }
  // }

  const controller2 = {
    getCategories,
    createCategories,
    deleteCategories,
    updateCategories
  };
  return controller2;
};
export default initializeDatabase_categories;

